DROP TABLE IF EXISTS `gs_article`;

CREATE TABLE `gs_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `model_id` smallint(6) NOT NULL DEFAULT '0' COMMENT '模型ID',
  `class_id` smallint(6) NOT NULL DEFAULT '0' COMMENT '分类ID',
  `title` varchar(128) DEFAULT '' COMMENT '标题',
  `alias` varchar(128) DEFAULT ' ' COMMENT '副标题',
  `tags` varchar(128) DEFAULT ' ' COMMENT '标签',
  `intro` varchar(255) CHARACTER SET utf8mb4 DEFAULT ' ' COMMENT '摘要',
  `content` longtext COMMENT '文章内容',
  `author` varchar(64) DEFAULT ' ' COMMENT '作者',
  `copyfrom` varchar(128) DEFAULT ' ' COMMENT '来源',
  `state` tinyint(1) NOT NULL DEFAULT '0' COMMENT '发布状态',
  `is_recommend` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否推荐',
  `is_slide` tinyint(4) DEFAULT '0' COMMENT '是否显示为幻片',
  `is_top` tinyint(4) DEFAULT '0' COMMENT '是否置顶',
  `include_pic` tinyint(1) NOT NULL DEFAULT '0' COMMENT '包含图片',
  `cover` varchar(128) DEFAULT '' COMMENT '封页图片',
  `slide` text COMMENT '轮播图',
  `slide_remark` text COMMENT '轮播图备注',
  `attachment` text COMMENT '附件',
  `hits` int(11) DEFAULT '0' COMMENT '点击数',
  `likes` int(11) unsigned DEFAULT '0' COMMENT '赞的数量',
  `oppose` int(11) DEFAULT '0' COMMENT '反对数量',
  `url` varchar(128) DEFAULT '' COMMENT '文件地址',
  `seo_title` varchar(128) DEFAULT '' COMMENT 'SEO标题',
  `seo_keywords` varchar(128) DEFAULT '' COMMENT 'SEO关键字',
  `seo_description` varchar(128) DEFAULT '' COMMENT 'SEO描述',
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

