CREATE TABLE `gs_comment` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `model_id` int(11) DEFAULT '0' COMMENT '模型ID',
  `content_id` int(11) DEFAULT '0' COMMENT '内容ID',
  `nickname` varchar(64) DEFAULT 'guest' COMMENT '昵称',
